import { formatDate } from '@/utils'
import {
  api,
} from './index'
import request from './request'
import store from '@/store' // 引入store
// customer
/** 企业相关 */
// 按企业ID查询企业详情
export const firmInfo = params => api(request.firmInfo, 'GET', params).then(res=>{
  // console.log(res)
  let day = res.data.finalFollowUpTime
  day = day.substring(0,19)    
  day = day.replace(/-/g,'/')
  day = new Date(day).getTime()
  let timestamp = parseInt(new Date().getTime())
  day = (timestamp - day)/1000/60/60/24
  day = day<=1 ? 1:day
  res.data.day = day
  return res
})


/** 联系人相关 */
// 联系人列表
export const callUserList = params => api(request.callUserList, 'GET', params)
// 新增联系人
export const createUser = params => api(request.createUser, 'POST', params)
// 删除联系人
export const deleteCallUser = params => api(request.deleteCallUser, 'DELETE', params)
// 联系人详情
export const callUserData = params => api(request.callUserData, 'GET', params)
// 修改联系人
export const updateCallUser = params => api(request.updateCallUser, 'PUT', params)
// 设置第一联系人
export const setTopCallUser = params => api(request.setTopCallUser, 'PUT', params)

/** 放贷相关 */
// 投放查询
export const lendPage = params => api(request.lendPage, 'GET', params).then(res=>{
  res.data.data.map(d=>{
    d.createTime =  formatDate(new Date(d.createTime),'yyyy-MM-dd hh:mm:ss')    
  })
  return res
})
// 新增投放项目（投放子项目）
export const addLend = params => api(request.addLend, 'POST', params)
// 投放项目查询(投放列表)
export const lendItem = params => api(request.lendItem, 'GET', params)
// 投放项目确认（风控和财务）
export const lendConfirm = params => api(request.lendConfirm, 'PUT', params)

/** 事件接口相关 */
//  新建事件（立项\报价\尽调\上会\签约）
export const addProject = params => api(request.addProject, 'POST', params)
// 动态查询（立项\报价\尽调\上会\签约）
export const projectList = params => api(request.projectList, 'GET', params).then(res => {
  let UID = store.getters.userInfo.id+''
  res.data.data = res.data.data || []
  res.data.data.map((d, idx) => {
    if(d.browseUsers){    
      d.isRead = d.browseUsers.indexOf(UID) > -1 ? true : false
    } 

    d.information = JSON.parse(d.information)
    d.information.companyName = d.companyName
    d.createTime = d.createTime
    d.updateTime = d.updateTime
    d.boaCreateTime = d.boaCreateTime ? d.boaCreateTime.slice(0, 10) : null
  })
  console.log(res.data)
  // console.log('projectList',res.data)
  return res.data
})
// 查询所有可关联项目列表（报价\尽调\上会\签约）
export const relation = params => api(request.relation, 'GET', params)
// 根据事件ID查询事件详情
export const idProject = params => api(request.idProject, 'GET', params).then(res => {
  res.data.information = JSON.parse(res.data.information)
  res.data.information.companyName = res.data.companyName
  return res.data
})

/** 通用接口相关 */
// 上传
export const upload = params => api(request.upload, 'POST', params)

/** 拜访接口相关 */
// 新建拜访
export const addVisit = params => api(request.Visit, 'POST', params)
// 修改拜访信息
export const putVisit = params => api(request.Visit, 'PUT', params)
// 根据拜访ID查询拜访
export const getVisit = params => api(request.Visit, 'GET', params)
// 动态查询拜访
export const VisitList = params => api(request.VisitList, 'GET', params)
// 根据项目ID查询可新建的事件集合
export const menuProject = params => api(request.menuProject, 'GET', params)

/**留言相关 */
//留言列表 messageList
export const messageList = params => api(request.messageList, 'GET', params).then(res=>{
  // console.log('留言列表 messageList',res)
  res.data.map(d=>{    
    d.createTime = formatDate(new Date(d.createTime),'yyyy-MM-dd hh:mm:ss')
    return d
  })
  return res
})
//增加留言 addMessage
export const addMessages = params => api(request.addMessage, 'POST', params)

/**协作人相关 */
//协作人信息列表
export const collaboratorList = params => api(request.collaboratorList, 'GET', params).then(res => {
  res.data.leaderUserVOS.map(d => {
    d.isSet = true
  })
  return res
})
//协作人信息回显 
export const collaboratorIdInfo = params => api(request.collaboratorIdInfo, 'GET', params)
//编辑协作人
export const collaboratorUpdate = params => api(request.collaboratorUpdate, 'POST', params)
//更改主负责人
export const leading = params => api(request.leading, 'PUT', params)
//置顶联系人
export const topUser = params => api(request.topUser, 'PUT', params)
