import {
  api,
} from './index'
// import { format } from '@/utils'
import request from './request'
import store from '@/store' // 引入store
// 贷款
// 生成和增加贷后金额
export const lendingInfo = params => api(request.lendingInfo, 'POST', params)
// 新增归还计划
export const fuLiRentBack = params => api(request.fuLiRentBack, 'POST', params)
// 新增检查列表和贷后资料 
export const rentBackFile = params => api(request.rentBackFile, 'POST', params)
// 贷后提醒
export const sendTextCardMessage = params => api(request.sendTextCardMessage, 'GET', params)
// 条件查询贷后管理列表
export const lendPageList = params => api(request.lendPageList, 'GET', params).then(res=>{
  let UID = store.getters.userInfo.id+''
  // console.log('// 条件查询贷后管理列表',res)
  res.data.data.map(d=>{
    if(d.browseUsers){
      // console.log('d.browseUsers',UID,d.browseUsers.indexOf(UID))
      d.isRead = d.browseUsers.indexOf(UID) > -1 ? true : false
    }  
    d.information = JSON.parse(d.information)
    // d.updateTime = d.updateTime.slice(0,10)
    d.createTime = d.updateTime.slice(0,10)
  })
  return res.data
})
// 查看归还机会详情
export const getfuLiRentBack = params => api(request.getfuLiRentBack, 'GET', params).then(res=>{
  res.data.information = JSON.parse(res.data.information)
  return res
})
// 查看当前贷后归还列表
export const findLendingInfoByIdList = params => api(request.findLendingInfoByIdList, 'GET', params).then(res=>{
  // console.log(res)
  if(res.data.data){
    res.data.data.map(d=>{
      d.information = JSON.parse(d.information)
    })
  }

  return res
})
// 条件查询归还列表
export const rentPageList = params => api(request.rentPageList, 'GET', params)
// 更改确认状态
export const updateAmountAndStatus = params => api(request.updateAmountAndStatus, 'PUT', params)
// 批量删除归还列表
export const deleteRentBackList = params => api(request.deleteRentBackList, 'DELETE', params)
// 条件查询检查列表和贷后资料 
export const rentBackFilePage = params => api(request.rentBackFilePage, 'GET', params).then(res=>{
  res.data.data.map(d=>{
    d.information = JSON.parse(d.information) || {}
    // d.updateTime = d.updateTime.slice(0,10)
    // d.createTime = d.updateTime
  })
  return res.data
})
// 删除检查列表和贷后资料 
export const updateRentBackFileById = params => api(request.updateRentBackFileById, 'PUT', params)
// 新增季度检查资料 
export const addSearonCheck = params => api(request.addSearonCheck, 'POST', params)
//新增贷后资料
export const addRentBack = params => api(request.addRentBack, 'POST', params)
//贷后季度检查确认完成
export const lendingInfoConfirm = params => api(request.lendingInfoConfirm, 'POST', params)
//请求贷后季度检查确认完成
export const getLendingInfoConfirm = params => api(request.lendingInfoConfirm, 'GET', params)
