import { isNull, addMark } from "@/utils";
import {} from "@/service/home";
import { setTimeout, clearTimeout } from "timers";

function rpx(v) {
  if (typeof v === "string") return v.slice(0, v.length - 2) * 1;
}

export default {
  props:{
    itm:{
      type: Object,
      default:()=>{
        return {}
      }
    }
  },
  name: "picItm",
  components: {},
  data() {
    return {
      flags: false,
      position: { x: 0, y: 0 },
      nx: "",
      ny: "",
      dx: "",
      dy: "",
      xPum: "",
      yPum: "",
      isShow:false,            
    };
  },
  watch: {},
  created() {},
  mounted() {
    let that = this;
    let dom = document.getElementById(this.itm.id)
      console.log('moveDiv',this.itm.id,dom,document.getElementById(this.itm.id))
  },
  destroyed() {},
  updated() {},
  methods: {
    // 实现移动端拖拽
    down() {
      console.log('down-event',event)
      let dom = document.getElementById(this.itm.id)
      this.flags = true;
      var touch;
      if (event.touches) {
        touch = event.touches[0];
      } else {
        touch = event;
      }
      this.position.x = touch.clientX;
      this.position.y = touch.clientY;
      this.dx = dom.offsetLeft;
      this.dy = dom.offsetTop;
    },
    move() {
      console.log('move-event',event,)
      let dom = document.getElementById(this.itm.id)


      if (this.flags) {
        var touch;
        if (event.touches) {
          touch = event.touches[0];
        } else {
          touch = event;
        }
        this.nx = touch.clientX - this.position.x;
        this.ny = touch.clientY - this.position.y;
        this.xPum = this.dx + this.nx;
        this.yPum = this.dy + this.ny;
        dom.style.left = this.xPum + "px";
        dom.style.top = this.yPum + "px";
        //阻止页面的滑动默认事件
        document.addEventListener(
          "touchmove",
          function() {
            // 1.2 如果碰到滑动问题，请注意是否获取到 touchmove
            // event.preventDefault(); //jq 阻止冒泡事件
            event.stopPropagation(); // 如果没有引入jq 就用 stopPropagation()
          },
          false
        );
      }
    },
    //鼠标释放时候的函数
    end() {
      this.flags = false;
    }
  }
};
